        var foo = new DigitalOcean.KubernetesCluster("foo", new DigitalOcean.KubernetesClusterArgs
        {
            Region = "nyc1",
            AutoUpgrade = true,
            Version = example.Apply(example => example.LatestVersion),
            NodePool = new DigitalOcean.Inputs.KubernetesClusterNodePoolArgs
            {
                Name = "default",
                Size = "s-1vcpu-2gb",
                NodeCount = 3,
            },
        });
    }

}
