                "2",
            ],
        ),
        digitalocean.GetSizesFilterArgs(
            key="regions",
            values=["sgp1"],
        ),
    ],
    sorts=[digitalocean.GetSizesSortArgs(
        key="price_monthly",
        direction="asc",
    )])
web = digitalocean.Droplet("web",
    image="ubuntu-18-04-x64",
    region="sgp1",
    size=main.sizes[0].slug)
```
