    privateKey: "file('key.pem')",
    leafCertificate: "file('cert.pem')",
});
const web = new digitalocean.Droplet("web", {
    size: "s-1vcpu-1gb",
    image: "ubuntu-18-04-x64",
    region: "nyc3",
});
const _public = new digitalocean.LoadBalancer("public", {
    region: "nyc3",
    forwardingRules: [{
        entryPort: 443,
        entryProtocol: "https",
        targetPort: 80,
        targetProtocol: "http",
        certificateName: cert.name,
    }],
    healthcheck: {
        port: 22,
        protocol: "tcp",
    },
    dropletIds: [web.id],
});
